package punto_02;

import java.util.ArrayList;


public class CollezioneMonete<T extends Valutabile<Integer>> {

	//variabili di istanza
	private ArrayList<T> collezione;
	
	public CollezioneMonete(ArrayList<T> collezione) {
		this.collezione = collezione;
		
	}
	
	//metodi dell'astrazione
	public int valoreMedio() {
		int somma = 0;
		int contatore = 0;
		for(int i = 0; i < collezione.size(); i++) {
			somma += collezione.get(i).valuta();
			contatore++;
		}
		return somma / contatore;
	}
	
	public T monetaMinima() {
		T min = collezione.get(0);
		for(int i = 0; i < collezione.size(); i++) {
			if(collezione.get(i).minimo() < min.minimo() )
				min = collezione.get(i);
		}
		return min;
	}
}

package punto_01;

import java.io.Serializable;

import punto_02.Valutabile;

public class MonetaInCorso extends MonetaConiata implements Valutabile<Integer>,Serializable{
	
	//variabili di istanza
	private Certificato certificato;
	
	//costruttore
	public MonetaInCorso(int valore, int annoConio, String nazione,Certificato certificato) {
		super(valore, annoConio, nazione);
		this.certificato = certificato;
	}
	
	//metodi di accesso
	public Certificato getCertificato() {
		return certificato;
	}
	//metodi modificatori
	public void setCertificato(Certificato certificato) {
		this.certificato = certificato;
	}

	//Override
	public String toString() {
		return super.toString()+"[certificato=" + certificato + "]";
	}


	public boolean equals(Object obj) {
		if (!super.equals(obj)) return false;
		MonetaInCorso other = (MonetaInCorso) obj;
		
		return certificato.equals(other.certificato);
	}
	
	public MonetaInCorso clone() {
		MonetaInCorso m;
		m = (MonetaInCorso) super.clone();
		m.certificato = this.certificato.clone();
		return m;
	}

	// implementazione metodi dell'interfaccia
	public Integer valuta() {
		
		return this.getValore();
	}

	public Integer minimo() {
		
		return this.getAnnoConio();
	}
}

package punto_03;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.ObjectInputStream;
import java.io.ObjectOutputStream;
import punto_01.Certificato;
import punto_01.Moneta;
import punto_01.MonetaFuoriCorso;
import punto_01.MonetaInCorso;

public class Test {
	
	
	public static void main(String[] args) {
		
		File unFile = new File("monete.dat");
		Moneta[] contenitore ;
		
		if(unFile.exists()) {
			System.out.println("Testo lettura");
			try {
				ObjectInputStream in;
				in = new ObjectInputStream(new FileInputStream(unFile));
				contenitore = (Moneta[]) in.readObject();
				System.out.println("ho letto queso array contenete i seguenti:");
				for(int i = 0; i < 4; i++) {
					System.out.println(contenitore[i].toString());
				}
				in.close();
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			} catch (IOException e) {
				e.printStackTrace();
			}catch (ClassNotFoundException e) {
				e.printStackTrace();
			}
			
		}else {
			MonetaInCorso monetaCorso1 = new MonetaInCorso(1, 1992,"Italia", new Certificato(123,"Stato"));
			MonetaInCorso monetaCorso2 = new MonetaInCorso(2, 1995,"Italia", new Certificato(123,"Stato1"));
			MonetaFuoriCorso monetaFuori1 = new MonetaFuoriCorso(5, 2000, "Italia", 2020);
			MonetaFuoriCorso monetaFuori2 = new MonetaFuoriCorso(6, 1990,"italia",2030);
			contenitore = new Moneta[4];
			contenitore[0] = monetaCorso1;
			contenitore[1] = monetaCorso2;
			contenitore[2] = monetaFuori1;
			contenitore[3] = monetaFuori2;
			
			System.out.println("testo scrittura:");
			try {
				ObjectOutputStream out = new ObjectOutputStream(new FileOutputStream(unFile));
				System.out.println("scrivo questi file:");
				for(int i = 0; i < contenitore.length; i++) {
					System.out.println(contenitore[i].toString());
				}
				out.writeObject(contenitore);
				out.close();
				
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			} catch (IOException e) {
				e.printStackTrace();
			}
			
		}
	}
}

package punto_01;

import java.io.Serializable;

public class Moneta implements Cloneable,Serializable{
	
	//variabili di istanza
	private int valore;
	
	//costruttore
	public Moneta(int valore) {
		this.valore = valore;
	}
	
	//metodi di accesso
	public int getValore() {
		return valore;
	}
	
	//metodi modificatori
	public void setValore(int valore) {
		this.valore = valore;
	}

	//Override
	public String toString() {
		return getClass().getName()+"[valore=" + valore + "]";
	}
	
	public boolean equals(Object obj) {
		if (obj == null) return false;
		if (getClass() != obj.getClass()) return false;
		Moneta other = (Moneta) obj;
		
		return valore == other.valore;
	}
	
	public Moneta clone() {
		try {
			return (Moneta) super.clone();
		} catch (CloneNotSupportedException e) {
			return null;
		}
	}
	
}

